import React,{useEffect,useState} from "react";
import { Button } from "react-bootstrap";
import Table from "react-bootstrap/Table";
import {Link} from 'react-router-dom'
import Search from "../util/search/search"
import axios from 'axios'
import Spinner from 'react-bootstrap/Spinner'
import './chefdept.css'




function ListeStages() {
   
  const [stages,setStages]=useState([]);
  const [loaded,setLoaded]=useState(false);

useEffect(() => {

  axios.get('http://localhost:5000/api/stages')
  .then(function (response) {
   

   
   console.log(response.data.stages);

    setStages(response.data.stages)
    setLoaded(true)
    
  })
  .catch(function (error) {
    
  })

}, [])





  return (
    <div className="container soumission-table">
      <div className="row header">Liste des Stages</div>
      <div className="row justify-content-end mb-5">
     <div className="col-sm-4">
     <Search/>
     </div>
     </div>
     

      <div className="table">

      { loaded ?
        <Table bordered hover >
          <thead className="tableHead">
            <tr>
              <th>Date de soumission</th>
              <th>Sujet</th>
              <th>Etudiants</th>
              <th>Action</th>
            </tr>
          </thead>
         
          <tbody>
          
           {stages.map(x =>{
           return( 
                    <tr>
                      
                        <td> <Link to="/chefdept/1" exact >{x.description} </Link>  </td>
                        <td> <Link to="/chefdept/2" >{x.dateDebut} </Link> </td>
                        <td><Link to="/chefdept/3"  > {x.etudiants.map(e=>{return (<div> {e} </div> )})} </Link></td>
                        <td className="buttons">  
                             <Button variant="success"><i class="fas fa-check mr-2"></i></Button>
                             <Button variant="danger"><i class="far fa-trash-alt"></i></Button></td>
                  
                  </tr>
                  )

          }) }

          
          </tbody>
         
        </Table>
        :  <Spinner animation="grow" />  }
      </div>
    </div>
  );
}

export default ListeStages;
