import { Form, Button, Table } from "react-bootstrap";
import React, { useState,useEffect, Fragment } from 'react'
import { connect } from "react-redux";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Input from "./Input";
import axios from 'axios'
import MultiSelect from "react-multi-select-component";
import ErrorModal from '../modals/ErrorModal';



const FormInfo = (props) => {

    const [startDate, setStartDate] = useState(new Date());
    const [finDate, setFinDate] = useState(new Date());
    const [email,setEmail] = useState('')
    const [tel,setTel] = useState('');
    const [organisme,setOrganisme] = useState('');
    const [rep,setRep] = useState('');
    const [encExterne,setEncExterne] = useState('');
    const [description,setDescription] = useState('');
    const [ville,setVille] = useState('')
    const [pays,setPays] = useState('')
    const [selectedProfs, setSelectedProfs] = useState([]);

    const [options,setOptions] = useState([]);

    const [show, setShow] = useState(false);
    const [error, setError] = useState("");
     //[
    //   { value: 'chocolate', label: 'Chocolate' },
    //   { value: 'strawberry', label: 'Strawberry' },
    //   { value: 'vanilla', label: 'Vanilla' }
    // ]
    useEffect(() => {
      axios.get('http://localhost:5000/api/enseignant')
      .then(function (response) {
        let t = [];

       
        response.data.enseignants.map(x=>{
          t.push({value:x._id,label:x.nom +" " + x.prenom})
        })

        setOptions(t)
        
      })
      .catch(function (error) {
        // handle error
        
      })
    }, [])


//   useEffect(() => {
//     axios({
//   method: 'Get',
//   url: 'https://football-web-pages1.p.rapidapi.com/goalscorers.json',
// params: {
//     comp: props.leagueSelected
//   },
// headers: {
//   'x-rapidapi-key': '61ebcf3d28msh3f225f6dc50cba5p124ba2jsn170cca6004b2',
//   'x-rapidapi-host': 'football-web-pages1.p.rapidapi.com'
// }
//  })
// .then(response => {
   
//    props.setResponseDataTable(response.data.goalscorers);
//    setLoadedTable(true);
   
// }) 
// .catch(err => {
//   console.log(err);
// })

// },props.leagueSelected);

    const submitStage = async event => {
      event.preventDefault();
      let profs=[];
       selectedProfs.map(x=>{
        profs.push(x.value)
       })

       console.log(props.userId)

      try {
          const response = await fetch('http://localhost:5000/api/stage/newstage',
          {
              method:'Post',
              headers:{
                  'Content-Type' : 'application/json',
                  'Accept' : 'application/json',
                  'Authorization' : 'Bearer <token_here>'
              
              },
              body:JSON.stringify({
                  email:email,
                  tel:tel,
                  organisme:organisme,
                  rep:rep,
                  encExterne:encExterne,
                  description:description,
                  ville:ville,
                  pays:pays,
                  selectedProfs:profs,
                  startDate:startDate.toISOString().substring(0, 10),
                  finDate:finDate.toISOString().substring(0, 10),
                  userId:props.userId

              })
          });


          const responseData = await response.json();

          if(!response.ok){
            setError(responseData.message)
