import React,{useState, useEffect} from 'react';
import axios from 'axios';

import ErrorModal from '../../modals/ErrorModal';
import { Form, Button, Table } from "react-bootstrap";
import Select from "react-select";
import Input from '../../form/Input';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./addStudent.css"
// import { set } from 'mongoose';


export default function AddStudent() {
  const [show, setShow] = useState(false);
  const [error, setError] = useState("");
  const [error2, setError2] = useState("");
  const [add, setAdd] = useState(true);
  const [adddManually, setAdddManually] = useState(true);
  const [filierTrigger, setFilierTrigger] = useState(false);
  const [optionTrigger, setOptionTrigger] = useState(false);
  const [filieres, setFiliere] = useState([]);

  const [fil, setFil] = useState([]);
  const [options, setOptions] = useState([]);


  const [Nom, setNom] = useState("");
  const [Prenom, setPrenom] = useState("");
  const [tel, setTel] = useState("");
  const [matricule, setMatricule] = useState("");
  const [cin, setCin] = useState("");
  const [cne, setCne] = useState("");
  const [genre, setGenre] = useState("");
  const [dateNaissance, setDateNaissance] = useState(new Date());
  //const [filieres, setFilieres] = useState([]);
  const [selectedFiliere, setSelectedFiliere ]  = useState("");
  const [selectedOption, setSelectedOption ]  = useState("");
  const [option, setOption] = useState([]);
  const [AnneeStage, setAnneeStage] = useState("");
  const [email, setEmail] = useState ("");

  const handleChange = (selectedFiliere) => {
    setSelectedFiliere(selectedFiliere);
    setFilierTrigger(true);

  //  let table2=[];
  //  const table =  option.filter(o => o.filiere == selectedFiliere.value );
  //  console.log(table);
  //  table.map((x) => {
  //   table2.push({ value: x._id, label: x.nomOption });
  //   })
  //   console.log(table2);
  //   setOption(table2);

  }


  const handleChangeOption = (selectedOption) => {
    setSelectedOption(selectedOption);
    setOptionTrigger(true);
    trigger();
  };

  const trigger = ()=>{
    if(filierTrigger && optionTrigger){
     setAdd(false);
     setAdddManually(false);
    }
  }
  
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/filieres")
      .then(function (response) {
        let t = [],opts=[];
        setFil(response.data.filieres)
        response.data.filieres.map((x) => {
          t.push({ value: x._id, label: x.nomFiliere });
        }) 
        
        setFiliere(t);
        
        // setrows(r)
      })
      .catch(function (error) {});
  }, [filieres]);
  
  useEffect(() => {

   const table =  []
   fil.map(f=>{

     if(f._id==selectedFiliere.value){
      f.options.map((x) => {
        table.push({ value: x._id, label: x.nomOption });
        })
     }
   })
     setOption(table)
        
      
      
  }, [selectedFiliere]);


  const addNewStudent = async (event) => {
    // add logic here.
  }
  
  
  const submitButtonStyle = {
    margin: "20px",
    position: "relative",
    width: "200px",
    height: "50px",
  };

  const formStyle = {
    marginTop: "10px",
    marginBottom: "10px",
    paddingTop: "10px",
  };
  return (
    <React.Fragment>
      <ErrorModal
        show={show}
        setShow={setShow}
        error={error}
        titre=""
        error2={error2}
      />
      <div className="row">
         <div className="col-lg-4 mb-5">
            <label >Choisir la filiere et l'option  de l'etudiant</label>
         </div>
         
         <div className="col-lg-4">
          <Select
              value={selectedFiliere}
              onChange={handleChange}
              options={filieres}
              placeholder = "filiere"
            />
          </div> 
          
          <div className="col-lg-4" style={{ textAlign: "left" }}>
            <Select
              value={selectedOption}
              onChange={handleChangeOption}
              options={option}
              placeholder = "Option"
            />
          </div>

     </div>
    
    {add ? "" :
     <div className="row">
      <div className="col-lg-6">
          <button type="button" class="btn btn-info add-new"><i className="fa fa-plus"></i> Ajouter Nouveau Etudiant</button>
      </div> 
      <div className="col-lg-6">
          <button type="button" className="btn btn-info add-new"><i className="fa fa-arrow-down-square-fill"></i> Importer un Fichier Excel</button>
      </div> 
     </div>
    }

    

      {adddManually ? "" : <Form style={formStyle}>
      <div className="row">
        <div className="col-lg-4">
          <Input
              id="formMatricule"
              label="Matricule"
              type="text"
              placeHolder="Matricule"
              className="text-muted"
              text=""
              value={matricule}
              inputValue={matricule}
              setInput={setMatricule}
          />
      </div>

          <div className="col-lg-4">
            <Input
              id="formNom"
              label="Nom"
              type="text"
              placeHolder="Nom"
              className="text-muted"
              text=""
              value={Nom}
              inputValue={Nom}
              setInput={setNom} 
           />
          </div>

          <div className="col-lg-4">
            <Input
              id="formPrenom"
              label="Prénom"
              type="text"
              placeHolder="Prénom"
              className="text-muted"
              text=""
              value={Prenom}
              inputValue={Prenom}
              setInput={setPrenom} 
          />
        </div>
    
      </div>

      <div className="row">
          <div className="col-lg-4">
            <Input
              id="formCni"
              label="CNI"
              type="text"
              placeHolder="Carte Nationale d'Identité"
              className="text-muted"
              text=""
              value={cin}
              inputValue={cin}
              setInput={setCin} 
           />
          </div>

          <div className="col-lg-4">
            <Input
              id="formCne"
              label="CNE"
              type="text"
              placeHolder="Code national de l'étudiant"
              className="text-muted"
              text=""
              value={cne}
              inputValue={cne}
              setInput={setCne} 
          />
        </div>
    
      <div className="col-lg-4">
          <Input
              id="formTelephone"
              label="Telephone"
              type="text"
              placeHolder="Numero de telephone"
              className="text-muted"
              text=""
              value={tel}
              inputValue={tel}
              setInput={setTel}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-lg-4">
          <Input
            id="formGenre"
            label="Genre"
            type="text"
            placeHolder="M (Masculin) ou F (Feminin)"
            className="text-muted"
            text=""
            value={genre}
            inputValue={genre}
            setInput={setGenre}
          />
        </div>

        <div className="col-lg-4" style={{ textAlign: "left" }}>
          <label>Filiere</label>

          <Select
            value={selectedFiliere}
            onChange={handleChange}
            options={filieres}
          />
        </div>

        <div className="col-lg-4" style={{ textAlign: "left" }}>
          <label>Options</label>

          <Select
            value={selectedOption}
            onChange={handleChangeOption}
            options={option}
          />
        </div>
      </div>

      <div className="row">
          <div className="col-lg-4">
            <Input
                id="formAnneeStage"
                label="Annee de Stage"
                type="text"
                placeHolder="Numero de telephone"
                className="text-muted"
                text=""
                value={AnneeStage}
                inputValue={AnneeStage}
                setInput={setAnneeStage}
            />
           </div>

           <div className="col-lg-4">
            <Input
              id="formEmail"
              label="Email"
              type="email"
              placeHolder="test@gmail.com"
              className="text-muted"
              value={email}
              inputValue={email}
              setInput={setEmail}
            />
            </div>

        <div className="col-lg-4" style={{ textAlign: "left" }}>
          
            <label>Date de Naissance </label>
          
          <DatePicker
            style={{ width: "inherit"}}
            selected={dateNaissance}
            onChange={(date) => {
              setDateNaissance(date);
            }}
          />
          
        </div>
      </div>

      <Button
        variant="primary"
        type="submit"
        style={submitButtonStyle}
        onClick={addNewStudent}
      >
        Ajouter Etudiant
      </Button>
      </Form>
    }
        </React.Fragment>
  )
}
