import React,{useEffect,useState} from "react";
import { Button } from "react-bootstrap";
import Table from "react-bootstrap/Table";
import {Link} from 'react-router-dom'
import Search from "../util/search/search"
import axios from 'axios'
import Spinner from 'react-bootstrap/Spinner'
import './chefdept.css'




function ListeStages() {
   
  const [stages,setStages]=useState([]);
  const [loaded,setLoaded]=useState(false);

useEffect(() => {

  axios.get('http://localhost:5000/api/stages')
  .then(function (response) {
   

   
   console.log(response.data.stages);

    setStages(response.data.stages)
    setLoaded(true)
    
  })
  .catch(function (error) {
    
  })

}, [])




function deleteHandler(e){

  axios({
   method: 'delete',
   url:'http://localhost:5000/api/stages/delete',
   headers: {}, 
   data: {
     stageId:e.target.value ,
   }

  })
  .then(function (response) {
    console.log(response.data);
   
    // setStages(response.data.stages)
    // setLoaded(true)
    
  })
  .catch(function (error) {
   
  })
}




  return (
    <div className="container chefdept">
      <div className="row header">Liste des Stages</div>
      <div className="row justify-content-end mb-5">
     <div className="col-sm-4">
     <Search/>
     </div>
     </div>
     

      <div className="table">

      { loaded ?
        <Table bordered hover >
          <thead className="tableHead">
            <tr>
              <th>Date de soumission</th>
              <th>Sujet</th>
              <th>Etudiants</th>
              <th>Action</th>
            </tr>
          </thead>
         
          <tbody>
          
           {stages.map(x =>{
           return( 
                    <tr key={x._id}> 
                      
                        <td> <Link  to="/chefdept/1" exact="true" >{x.description} </Link>  </td>
                        <td > <Link  to="/chefdept/2" >{x.dateDebut} </Link> </td>
                        <td ><Link  to="/chefdept/3"  > {x.etudiants.map(e=>{return (<div key={e._id}> {e.nom+" " +e.prenom} </div> )})} </Link></td>
                        <td  className="buttons">  
                             <Button   value={x._id} className="mr-1" variant="success"><i className="fas fa-check"></i></Button>
                             <Button  onClick={deleteHandler}  value={x._id} variant="danger"><i className="far fa-trash-alt"></i></Button>
                            </td>
                  
                  </tr>
                  )

          }) }

          
          </tbody>
         
        </Table>
        :  <Spinner animation="grow" />  }
      </div>
    </div>
  );
}

export default ListeStages;
