import React,{useState,useEffect} from 'react';
import { useParams } from 'react-router';
import './conversation.css';
import Message from './Messages/Message'
import { connect } from "react-redux";
import axios from 'axios'


function Conversation(props){

const [message, setmessage] = useState('')
const [chats,setChats] = useState([]);
const [loaded,setLoaded]=useState(false);



const to= useParams().to;


useEffect(() => {
    axios({
  url: 'http://localhost:5000//api/conversation/fetchMessages/'+to,
  method: 'Get',
  body:{
    user:props.userId
  },
  headers: { "Content-Type": "application/json"},

 })
.then(response => {

    console.log(response.data)
   setChats(response.data.chats)
   setLoaded(true)
   
}) 
.catch(err => {
  console.log(err);
})

},[]);



async function sendMessage(e){

    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/conversation/send',
      {
          method:'POST',
          headers:{
              'Content-Type' : 'application/json',
              'Accept' : 'application/json'
          
          },
          body:JSON.stringify({
            message:message,
            to:to,
            user:props.userId

          })
      });
      const responseData = await response.json();
  
              console.log(responseData)
              
      
        
        } catch (error) {
            console.log(error);
            
        }
  
   }

    
    return(
        <div className="container">

            <div className="container-fluid bg-light" id="chat">
            <div className="content d-flex flex-column" id="chat-content">

                <Message/>
                <Message/>
                <Message/>
   

            </div>
            <div className="tools form-row">
                <input onChange={(e)=>{ setmessage(e.target.value) }} id="newMessage" className="form-control col mr-2" type="text" name="" placeholder="Message"  ></input>

                <button onClick={sendMessage} className="btn btn-primary " type="button">
                <i className="far fa-paper-plane"></i>
                </button>
            </div>
            </div>
  </div>
    )
}


const mapStateToProps = state =>{
  return {
    
    userId: state.userId,
  }
}


export default connect(mapStateToProps,null)(Conversation);
