import React, { useState, useEffect, Fragment } from "react";
import Select from "react-select";
import { DataGrid } from '@material-ui/data-grid';
import axios from "axios";
const columns = [
  { field: 'id', headerName: 'ID', width: 90 },
  { field: 'firstName', headerName: 'First name', width: 150 },
  { field: 'lastName', headerName: 'Last ', width: 150 },
  { field: 'age',headerName: 'Age',type: 'number',width: 110,},
  { field: 'fullName',headerName: 'Full name',description: 'This column has a value getter and is not sortable.',
                  sortable: false,
                  width: 160,
    valueGetter: (params) =>
      `${params.getValue(params.id, 'firstName') || ''} ${
        params.getValue(params.id, 'lastName') || ''
      }`,
  },
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: 14 },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

export default function Student() {

  const [selectedFiliere, setSelectedFiliere] = useState("");

  const [filieres, setFiliere] = useState([]);

  const handleChange = (selectedFiliere) => {
    setSelectedFiliere(selectedFiliere);
  };
  

    //getting filieres

    useEffect(() => {
      axios
        .get("http://localhost:5000/api/filieres")
        .then(function (response) {
          let t = [];
  
          response.data.filieres.map((x) => {
            t.push({ value: x._id, label: x.nomFiliere });
          });
          setFiliere(t);
        })
        .catch(function (error) {});
    }, []);

  return (
    <div>

              <Select
              value={selectedFiliere}
              onChange={handleChange}
              options={filieres}
            />
    
    <div style={{ height: 400, width: '100%' }}>
      <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection />
    </div>

    </div>
  )
}
