import { Form, Button, Table } from "react-bootstrap";
import React, { useState,useEffect } from 'react'
import { connect } from "react-redux";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Input from "./Input";
import axios from 'axios'
import MultiSelect from "react-multi-select-component";



const FormInfo = (props) => {

    const [startDate, setStartDate] = useState(new Date());
    const [finDate, setFinDate] = useState(new Date());
    const [email,setEmail] = useState('')
    const [tel,setTel] = useState('');
    const [organisme,setOrganisme] = useState('');
    const [rep,setRep] = useState('');
    const [encExterne,setEncExterne] = useState('');
    const [description,setDescription] = useState('');
    const [ville,setVille] = useState('')
    const [pays,setPays] = useState('')
    const [selectedProfs, setSelectedProfs] = useState([]);

    const [options,setOptions] = useState([]);
     //[
    //   { value: 'chocolate', label: 'Chocolate' },
    //   { value: 'strawberry', label: 'Strawberry' },
    //   { value: 'vanilla', label: 'Vanilla' }
    // ]
    useEffect(() => {
      axios.get('http://localhost:5000/api/enseignant')
      .then(function (response) {
        let t = [];

       
        response.data.enseignants.map(x=>{
          t.push({value:x._id,label:x.nom +" " + x.prenom})
        })

        setOptions(t)
        
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
    }, [])


//   useEffect(() => {
//     axios({
//   method: 'Get',
//   url: 'https://football-web-pages1.p.rapidapi.com/goalscorers.json',
// params: {
//     comp: props.leagueSelected
//   },
// headers: {
//   'x-rapidapi-key': '61ebcf3d28msh3f225f6dc50cba5p124ba2jsn170cca6004b2',
//   'x-rapidapi-host': 'football-web-pages1.p.rapidapi.com'
// }
//  })
// .then(response => {
   
//    props.setResponseDataTable(response.data.goalscorers);
//    setLoadedTable(true);
   
// }) 
// .catch(err => {
//   console.log(err);
// })

// },props.leagueSelected);

    const submitStage = async event => {
      event.preventDefault();

      try {
          const response = await fetch('http://localhost:5000/api/users/login',
          {
              method:'Post',
              headers:{
                  'Content-Type' : 'application/json',
                  'Accept' : 'application/json',
                  'Authorization' : 'Bearer <token_here>'
              
              },
              body:JSON.stringify({
                  email:email,
                  tel:tel,
                  organisme:organisme,
                  rep:rep,
                  encExterne:encExterne,
                  description:description,
                  ville:ville,
                  pays:pays,
                  selectedProfs:selectedProfs,
                  startDate:startDate,
                  finDate:finDate,
                  userId:props.userId

              })
          });


          const responseData = await response.json();

          if(!response.ok){
          //  setError(responseData.message)
          //  setShow(true);
           }
          else{
              // props.onLoginSucceed(responseData.user._id,responseData.user)
              // console.log(responseData.user)
              // props.history.push('/student');
          }

          


          
      
      } catch (error) {
          
      }
  }


   


    
   


  const formStyle = {
    // marginLeft: 250,
    // marginRight: 350,
    // border: "solid 1px",
    // boxShadow: "2px 2px",
    marginTop: "10px",
    marginBottom: "10px",
    // textAlign: "center",
    paddingTop: "10px",
  };
  const submitButtonStyle = {
    margin: "20px",
    position: "relative",
    width: "200px",
    height: "50px",
  };
  return (
    <Form style={formStyle}>
      <div className="row">
        <div className="col-lg-6">
        
          <Input
            id="formMatricule"
            label="Matricule"
            type="text"
            placeHolder={props.userInfo.matricule}
            className="text-muted"
            text=""
            readonly="true"
          />

        </div>
        <div className="col-lg-6">
          <Input
            id="formNom"
            label="Nom"
            type="text"
            placeHolder={props.userInfo.nom}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>
      </div>
      <div className="row">
        <div className="col-lg-6">
          <Input
            id="formPrenom"
            label="Prenom"
            type="text"
            placeHolder={props.userInfo.prenom}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>
        <div className="col-lg-6">
          <Input
            id="formCIN"
            label="CIN"
            type="text"
            placeHolder={props.userInfo.cin}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>
      </div>
      <div className="row">
        <div className="col-lg-6">
          <Input
            id="formCNE"
            label="CNE"
            type="text"
            placeHolder={props.userInfo.cne}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>

        <div className="col-lg-6">
          <Input
            id="formDateNaissance"
            label="Date de Naissance"
            type="text"
            placeHolder={props.userInfo.dateNaissance}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>
      </div>

      <div className="row">
        <div className="col-lg-6">
          <Input
            id="formGenre"
            label="Genre"
            type="text"
            placeHolder={props.userInfo.genre}
            className="text-muted"
            text=""
            readonly="true"
          />
        </div>

        <div className="col-lg-6">
          <Input
            id="formFiliere"
            label="Filiere"
            type="text"
            placeHolder=""
            className="text-muted"
            readonly="true"
          />
        </div>
      </div>

      <div className="row">
        <div className="col-lg-6">
        <Input
            id="formAnnee"
            label="Année"
            type="text"
            placeHolder={props.userInfo.promotion}
            className="text-muted"
            readonly="true"
          />
         
        </div>
        <div className="col-lg-6">
         
        <Input
            id="formEmail"
            label="Email"
            type="email"
            placeHolder="test@gmail.com"
            className="text-muted"
            inputValue={email}
            setInput={setEmail}
          />
          
        </div>
      </div>

      <div className="row">
        <div className="col-lg-6">
      
          <Input
            id="formTelephone"
            label="Telephone"
            type="text"
            placeHolder="0652485514"
            className="text-muted"
            text=""
            inputValue={tel}
            setInput={setTel}
          />
        </div>

        <div className="col-lg-6">
          <Input
            id="formOrganismeAcceuil"
            label="Organisme D'acceuil"
            type="text"
            placeHolder="emi"
            className="text-muted"
            inputValue={organisme}
            setInput={setOrganisme}
          />
        </div>
      </div>
   
      <div className="row">
        <div className="col-lg-6">
          <Input
            id="formPostDuRepresentant"
            label="
            Poste Du Representant"
            type="text"
            placeHolder="Chef de Projet Senior SI"
            className="text-muted"
            inputValue={rep}
            setInput={setRep}
          />
        </div>

        <div className="col-lg-6">
          <Input
            id="formEncadrantExterne"
            label='Encadrant Externe'
            type="text"
            placeHolder=""
            className="text-muted"
            inputValue={encExterne}
            setInput={setEncExterne}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-lg-6" style={{textAlign:"left"}}>

              <label  >
              Encadrant interne
              </label>

            <MultiSelect
                options={options}
                value={selectedProfs}
                onChange={setSelectedProfs}
                labelledBy={"Select"} />
  
        </div>
        <div className="col-lg-6">

          <Input
            id="formTitreStage"
            label="Titre De Stage"
            type="text"
            placeHolder="..."
            className="text-muted"
            inputValue={description}
            setInput={setDescription}
          />
        </div>
      </div>

      <div className="row">
      <div className="col-lg-6" style={{textAlign:"left"}}>
         <div>
         <label  >
        Date Debut
        </label>
        </div> 
        <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
        </div>
       
        <div className="col-lg-6" style={{textAlign:"left"}}>
         <div>
         <label  >
        Date Fin
        </label>
        </div>
         <DatePicker style={{width:"100%"}} selected={finDate} onChange={date => {setFinDate(date); console.log(finDate.toISOString().substring(0, 10))}} />
        </div>
      </div>

      <div className="row">
        <div className="col-lg-6">
          <Input
            id="formVilleStage"
            label="Ville de Stage"
            type="text"
            placeHolder="Rabat"
            className="text-muted"
            inputValue={tel}
            setInput={setTel}
          />
        </div>
        

        <div className="col-lg-6">
          <Input
            id="formPaysStage"
            label="Pays de Stage"
            type="text"
            placeHolder="Maroc"
            className="text-muted"
            inputValue={tel}
            setInput={setTel}
          />
        </div>
      </div>

      <Button variant="primary" type="submit" style={submitButtonStyle} onClick={()=>{ console.log(selectedProfs)}} >
        Submit
        
      </Button>
    </Form>
  );
};

const mapStateToProps = (state) => {
  return {
    isLoged: state.isLoged,
    userInfo: state.userInfo,
  };
};

